mapper.insert:
INSERT INTO members ( member_id, member_pw, member_name, member_email, member_phoneNo, member_addr, member_detail_addr, member_date, naver_login_cd, kakao_login_cd ) VALUES ( #{member_id}, #{member_pw}, #{member_name}, #{member_email}, #{member_phoneNo}, #{member_addr}, #{member_detail_addr}, #{member_date},
#{naver_login_cd}
'' ,
#{kakao_login_cd}
''
)

mapper.select:
SELECT 
    COUNT(*)
FROM 
    members
WHERE 
    member_name = #{member_name}
    AND member_email = #{member_email}
    AND member_id = #{member_id}

mapper.update:
UPDATE members
SET 
member_pw = #{member_pw}        
WHERE member_id = #{member_id}
