mapper.select:
SELECT rownum, b.book_isbn, b.book_name, b.book_price, b.book_publisher, b.book_author, b.book_country_type, b.book_category, b.book_state, b.book_register_date FROM ( SELECT book_isbn, book_name, book_price, book_publisher, book_author, book_country_type, book_category, book_state, book_register_date FROM books WHERE book_deleted = '01'
AND book_state = #{book_state}
AND book_register_date >= #{start_date}
AND book_register_date <= #{end_date}
AND book_isbn LIKE CONCAT('%', #{word}, '%')
AND book_name LIKE CONCAT('%', #{word}, '%')
ORDER BY book_register_date DESC ) b

mapper.update:
UPDATE
books
SET
book_state = #{book_state}
WHERE
book_isbn = #{book_isbn}

UPDATE
books
SET
book_deleted = #{book_deleted}
WHERE
book_isbn = #{book_isbn}
