mapper.select:
SELECT * FROM ( SELECT i.inquiry_num, i.inquiry_title, i.inquiry_content, i.member_id, i.inquiry_write_date, i.inquiry_answer_status, i.inquiry_type, i.inquiries_original, i.inquiries_changed, i.order_num, i.order_detail_num, i.order_qty, ia.answer_content, ia.answer_write_date, ROW_NUMBER() OVER (ORDER BY i.inquiry_write_date ASC) AS row_num FROM inquiries i LEFT JOIN inquiries_answer ia ON i.inquiry_num = ia.inquiry_num WHERE i.member_id = #{inquiriesDTO.member_id}
AND i.inquiry_answer_status = #{inquiriesDTO.inquiry_answer_status}
AND i.inquiry_write_date BETWEEN #{inquiriesDTO.startDate} AND #{inquiriesDTO.endDate}
) WHERE row_num BETWEEN #{startRow} AND #{endRow}

SELECT 
	COUNT(*) 
FROM 
	inquiries i
WHERE 
	i.member_id = #{inquiriesDTO.member_id}

    AND i.inquiry_answer_status = #{inquiriesDTO.inquiry_answer_status}


    AND i.inquiry_write_date BETWEEN #{inquiriesDTO.startDate} AND #{inquiriesDTO.endDate}

SELECT DISTINCT o.order_num, o.order_purchase_date, b.book_name, d.order_detail_num, d.order_detail_qty, d.order_request_qty FROM Customer_Orders o JOIN Customer_Order_Details d ON o.order_num = d.order_num JOIN Books b ON d.book_isbn = b.book_isbn WHERE o.member_id = #{member_id}
AND o.order_purchase_date BETWEEN #{startDate} AND #{endDate}
ORDER BY o.order_purchase_date ASC

mapper.update:
UPDATE 
	Customer_Order_Details
SET 
	order_request_qty = #{order_request_qty}

WHERE
	order_detail_num = #{order_detail_num}
