mapper.select:
SELECT * FROM (
    SELECT * FROM notices
    WHERE notice_num < #{noticeNum}
    AND notice_visible != '02'

        AND notice_title LIKE #{keyword}

    ORDER BY notice_num DESC
)
WHERE ROWNUM = 1

SELECT * FROM (
    SELECT * FROM notices
    WHERE notice_num > #{noticeNum}
    AND notice_visible != '02'

        AND notice_title LIKE #{keyword}

    ORDER BY notice_num ASC
)
WHERE ROWNUM = 1
